       const write = async ctx => {
  const schema = Joi.object().keys({
    //��ü�� ���� �ʵ带 ������ ������ ����
    title: Joi.string().required(),
    category: Joi.string().required(),
    status: Joi.string().required(),
    date: Joi.string().required(),
    place: Joi.string().required(),
    description: Joi.string().required(),
  });

  //���� ��, ������ ��� ����ó��
  const result = Joi.validate(ctx.request.body, schema);
  if (result.error) {
    ctx.status = 400;
    ctx.body = result.error;
    return;
  }

  const {
    title,
    category,
    status,
    date,
    place,
    description,
  } = ctx.request.body;
  const post = new Post({
    title,
    category,
    status,
    date,
    place,
    description,
    user: ctx.state.user, //����Ʈ �ۼ��� �α����� ���� ������ ����Ʈ ��ü�� ����
  });
  try {
    await post.save();
    ctx.body = post;
  } catch (e) {
    ctx.throw(500, e);
  }
};

//removes html tags, slices paragraph.
const removeHtmlAndShorten = body => {
  const filtered = sanitizeHtml(body, {
    allowedTags: [],
    //   'h1',
    //   'h2',
    //   'b',
    //   'i',
    //   'u',
    //   's',
    //   'p',
    //   'ul',
    //   'ol',
    //   'li',
    //   'blockquote',
    //   'a',
    //   'img',
    // ],
    // allowedAttributes: {
    //   a: ['href', 'name', 'target'],
    //   img: ['src'],
    //   li: ['class'],
    // },
    // allowedSchemes: ['data', 'http'],
  });
  return filtered.length < 300 ? filtered : `${filtered.slice(0, 300)}...`;
};

//������ ��ȸ
       const list = async ctx => {
  //http://localhost:4000/api/posts?page=2 �� �������� �����Ͽ� ��ȸ
  //page �������� ������ �� ���� int�� �Ľ��ϰ�, ������ 1�� �Ľ�
  const page = parseInt(ctx.query.page || '1', 5);

  if (page < 1) {
    ctx.status = 400;
    return;
  }

  const { username } = ctx.query;
  //??
  const query = {
    //username, ���� ��ȿ�ϸ� ��ü �ȿ� �ְ�, �ƴϸ� ���� ����
    ...(username ? { 'user.username': username } : {}),
  };

  try {
    //�ֽż� ����, �ѹ��� ã�� �����͸� 5���� ����, (�־��� page������ - 1) * 5���� �����ʹ� �ǳʶ�.
    //posts�� ��ü�� �迭
    const posts = await Post.find(query)
      .sort({ _id: -1 })
      .limit(5)
      .skip((page - 1) * 5)
      .exec();
    //postCount�� db ���� document(row)�� ����
    const postCount = await Post.countDocuments().exec();
    //Last-Page��� Ŀ���� HTTP ������� document ����/5 + 1�� ����.
    ctx.set('Last-Page', Math.ceil(postCount / 5));
    //three dotǥ�� ����, ������Ʈ�� ������Ƽ�� ������ �� �Ȱ��� ������Ƽ�� ������ �ڿ� ���ɷ� ������.
    ctx.body = posts
      .map(post => post.toJSON())
      .map(post => ({
        ...post,
        description: removeHtmlAndShorten(post.description),
      }));
  } catch (e) {
    ctx.throw(500, e);
  }
};
