       const read = ctx => {
  ctx.body = ctx.state.post;
};
       const remove = async ctx => {
  const { id } = ctx.params;
  try {
    await Post.findByIdAndRemove(id).exec();
    ctx.status = 204;
  } catch (e) {
    ctx.throw(500, e);
  }
};
       const update = async ctx => {
  const schema = Joi.object().keys({
    //��ü�� ���� �ʵ带 ������ ������ ����
    title: Joi.string(),
    category: Joi.string(),
    status: Joi.string(),
    date: Joi.string(),
    place: Joi.string(),
    description: Joi.string(),
  });

  //���� ��, ������ ��� ����ó��
  const result = Joi.validate(ctx.request.body, schema);
  if (result.error) {
    ctx.status = 400;
    ctx.body = result.error;
    return;
  }

  const { id } = ctx.params;
  try {
    const post = Post.findByIdAndUpdate(id, ctx.request.body, {
      new: true,
    }).exec();
    if (!post) {
      ctx.status = 404;
      return;
    }
    ctx.body = post;
  } catch (e) {
    ctx.throw(500, e);
  }
};
