const { ObjectId } = mongoose.Types;
//params로 들어오는 id가 유효한 ObjectId인지 검사하는 미들웨어
       const checkObjectId = (ctx, next) => {
  const { id } = ctx.params;
  if (!ObjectId.isValid(id)) {
    ctx.status = 400;
    return;
  }
  return next();
};

//포스트 작성, async로 비동기 처리.
       const write = async ctx => {
  //객체의 필드를 검증하기 위함
  const schema = Joi.object().keys({
    title: Joi.string().required(),
    body: Joi.string().required(),
    taggedContest: Joi.string().required(),
    videoURL: Joi.string(),
    team: Joi.string().required(),
    status: Joi.string().required(),
    stars: Joi.number(),
    comments: Joi.array().items(Joi.object()),
  });

  //객체 필드 검증 결과가 result에 저장.
  const result = Joi.validate(ctx.request.body, schema);
  if (result.error) {
    ctx.status = 400; //bad request
    ctx.body = result.error;
    return;
  }

  const {
    title,
    body,
    taggedContest,
    videoURL,
    team,
    status,
  } = ctx.request.body;
  const content = new Content({
    title,
    body,
    taggedContest,
    videoURL,
    team,
    status,
    stars: 0,
    comments: [],
  });
  try {
    await content.save();
    ctx.body = content;
  } catch (e) {
    ctx.throw(500, e);
  }
};

//포스트 목록 조회
       const list = async ctx => {
  try {
    //Content는 content 모델
    const contents = await Content.find().exec();
    ctx.body = contents;
  } catch (e) {
    ctx.throw(500, e);
  }
};

//특정 id를 갖는 포스트 조회
       const read = async ctx => {
  const { id } = ctx.params;
  try {
    const content = await Content.findById(id).exec();
    //받은 id에 해당하는 content가 존재하지 않으면
    if (!content) {
      ctx.status = 404;
      return;
    }
    ctx.body = content;
  } catch (e) {
    ctx.throw(500, e);
  }
};

//특정 id를 갖는 content 삭제
       const remove = async ctx => {
  const { id } = ctx.params;
  try {
    await Content.findByIdAndRemove(id).exec();
    ctx.status = 204; // nothing to return.
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const update = async ctx => {
  //객체의 필드를 검증하기 위함
  const schema = Joi.object().keys({
    title: Joi.string(),
    body: Joi.string(),
    taggedContest: Joi.string(),
    videoURL: Joi.string(),
    team: Joi.string(),
    status: Joi.string(),
    stars: Joi.number(),
    comments: Joi.array().items(Joi.object()),
  });

  //객체 필드 검증 결과가 result에 저장.
  const result = Joi.validate(ctx.request.body, schema);
  if (result.error) {
    ctx.status = 400; //bad request
    ctx.body = result.error;
    return;
  }

  const { id } = ctx.params;
  try {
    const content = await Content.findByIdAndUpdate(id, ctx.request.body, {
      new: true,
    }).exec();
    if (!content) {
      ctx.status = 404;
      return;
    }
    ctx.body = content;
  } catch (e) {
    ctx.throw(500, e);
  }
};
