import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';

const ToTopButtonHolder = styled.button`
  width: 3.5rem;
  height: 3.5rem;
  position: fixed;
  bottom: 7rem;
  right: 0.8rem;
  border-radius: 5px;
  background: ${palette.mainColor};
  opacity: 0.8;
  transition: 0.3s;
  text-align: center;
  padding-top: 0.9rem;
  color: white;
  border: none;

  &:hover {
    opacity: 1;
  }
`;

const ToTopButton = () => {
  const toTop = () => {
    window.scrollTo(0, 0);
  };
  return <ToTopButtonHolder onClick={toTop}>Top</ToTopButtonHolder>;
};

export default ToTopButton;
