import React from 'react';
import styled from 'styled-components';
import Responsive from '../common/Responsive';
import Button from '../common/Button';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';

const FeaturedWorksListHolder = styled(Responsive)`
  margin-top: 5rem;
  margin-bottom: 5rem;
  padding-top: 5rem;
  padding-bottom: 5rem;
`;

const FeaturedWorksWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const FeaturedWorkItem = styled(Link)`
  padding: 2rem;
  margin: auto;
  margin-bottom: 2.5rem;
  border: 1px solid ${palette.gray[5]};
  width: 270px;
  height: 400px;
  h1 {
    font-size: 30px;
    border-bottom: 1px solid ${palette.gray[5]};
    padding-bottom: 1rem;
    color: ${palette.gray[8]};
    font-weight: 500;
  }
  h2 {
    font-size: 25px;
    padding-bottom: 1rem;
    color: #ff4e50;
  }
  @media (max-width: 1152px) {
    width: 230px;
  }
  @media (max-width: 768px) {
    width: 48%;
`;

const StyledH1 = styled.h1`
  font-size: 35px;
  font-weight: lighter;
  padding-bottom: 1rem;
`;

const Spacer = styled.div`
  height: 16rem;
`;

const FeaturedWorks = ({ featuredWorks }) => {
  return (
    <FeaturedWorksListHolder>
      <StyledH1>Featured Works</StyledH1>
      <FeaturedWorksWrapper>
        {featuredWorks.map(featuredItem => {
          return (
            <FeaturedWorkItem to={`/content/${featuredItem._id}`}>
              <h1>{featuredItem.title}</h1>
              <h2>{featuredItem.stars}</h2>
            </FeaturedWorkItem>
          );
        })}
      </FeaturedWorksWrapper>
      <Spacer />
    </FeaturedWorksListHolder>
  );
};

export default FeaturedWorks;
