import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { readPost, unloadPost } from '../../modules/post';
import PostViewer from '../../components/post/PostViewer';
import PostActionButtons from '../../components/post/PostActionButtons';
import { setOriginalPost } from '../../modules/write';
import { removePost } from '../../lib/api/posts';
import { setContestName } from '../../modules/contestName';

const PostViewerContainer = ({ match, history }) => {
  const { postId } = match.params;
  const dispatch = useDispatch();
  //post === state.post.post, error === state.post.error, ...
  const { post, error, loading, user } = useSelector(
    ({ post, loading, user }) => ({
      //state.post, state.loading, state.user
      post: post.post,
      error: post.error,
      loading: loading['post/READ_POST'],
      user: user.user,
    }),
  );

  useEffect(() => {
    dispatch(readPost(postId));
    //대회 페이지가 로드되었을 때, 스토어의 contestName상태를 업데이트.
    dispatch(setContestName(post.title));
    return () => {
      dispatch(unloadPost());
    };
  }, [dispatch, postId, post.id]);

  const onEdit = () => {
    dispatch(setOriginalPost(post));
    history.push('/write');
  };

  const onRemove = async () => {
    try {
      await removePost(postId);
      history.push('/postlist');
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <PostViewer
      post={post}
      loading={loading}
      error={error}
      user={user}
      actionButtons={<PostActionButtons onEdit={onEdit} onRemove={onRemove} />}
    />
  );
};

export default withRouter(PostViewerContainer);
