import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';
import { Link } from 'react-router-dom';

//배경화면을 위해 responsive div를 감싸는 wrapper
const MainSubMenuBackground = styled.div`
  padding-top: 3rem;
  width: 100%;
  background-image: url('https://cdn.pixabay.com/photo/2017/05/13/18/36/cat-2310384_960_720.jpg');
  @media (max-width: 768px) {
    margin-top: 2rem;
  }
`;

//responsive design을 위한 wrapper
const MainSubMenuHolder = styled(Responsive)`
  height: 20rem;
  display: flex;
  justify-content: space-between;
  @media (max-width: 1152px) {
    height: 15rem;
  }
  @media (max-width: 768px) {
    height: 10rem;
  }
`;

const SubMenuItemHolder = styled.div`
  width: 17%;
  border: 1px solid green;
`;

//원의 가로 세로 비율을 맞추기 위해 각각의 SubMenuCircle을 감싸는 div를 따로 설계
const SubMenuCircleHolder = styled.div`
  position: relative;
  width: 100%;
  padding-top: 100%;
  border: 1px solid red;
`;

//클릭시 해당 메뉴로 이동하는 원모양 SubMenu 아이템들.
const SubMenuCircle = styled(Link)`
  display: flex;
  position: absoute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  border: 1px solid blue;
  border-radius: 50%;
`;

//각각의 SubMenuCircle에 대한 설명을 감싸는 div
const SubMenuItemTitleHolder = styled.div`
  width: 100%;
  text-align: center;
  margin-top: 1rem;
  h1 {
    font-size: 1.25rem;
    margin: 0;
    font-weight: 500;
    color: ${palette.gray[9]};
  }
  p {
    font-size: 1.25rem;
    margin: 0;
    color: ${palette.gray[7]};
  }
`;

const MainSubMenuItem = () => {
  return (
    <SubMenuItemHolder>
      <SubMenuCircleHolder>
        <SubMenuCircle />
      </SubMenuCircleHolder>
      <SubMenuItemTitleHolder>
        <h1>asdf</h1>
        <p>asdfasdf</p>
      </SubMenuItemTitleHolder>
    </SubMenuItemHolder>
  );
};

const MainSubMenu = () => {
  return (
    <MainSubMenuBackground>
      <MainSubMenuHolder></MainSubMenuHolder>
    </MainSubMenuBackground>
  );
};

export default MainSubMenu;
