import React from 'react';
import styled from 'styled-components';
import Responsive from '../common/Responsive';
import Button from '../common/Button';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';
import FeaturedWorks from './FeaturedWorks';
import ErrorNotifier from '../common/ErrorNotifier';

const MainViewerBlock = styled(Responsive)`
  margin-top: 3rem;
  text-align: center;
`;

const StaredWorks = styled.div`
  margin-top: 5rem;
  margin-bottom: 5rem;
  padding-top: 5rem;
  padding-bottom: 5rem;
`;

const StaredWorksWrapper = styled.div`
  display: flex;
`;

const StaredWorkItem = styled.div`
  padding: 2rem;
  margin: 0.5rem;
  border: 1px solid ${palette.gray[5]};
  width: 300px;
  height: 400px;
  h1 {
    font-size: 30px;
    border-bottom: 1px solid ${palette.gray[5]};
    padding-bottom: 1rem;
  }
  h2 {
    font-size: 25px;
    padding-bottom: 1rem;
    color: #ff4e50;
  }
`;

const StyledH1 = styled.h1`
  font-size: 35px;
  font-weight: lighter;
  border-bottom: 1px solid ${palette.gray[5]};
  padding-bottom: 1rem;
`;

const Spacer = styled.div`
  height: 16rem;
`;

const FeatureBox = styled.div`
  width: '100%',
  height: '300px',
  background: '#ff9698',
  display: 'flex'
`;

const MainViewer = ({ contents, loading, error }) => {
  if (error) {
    return (
      <ErrorNotifier
        errorTitle="ERROR!"
        errorMessage="페이지를 로드할 수 없습니다."
      />
    );
  }

  if (!loading && contents) {
    contents.sort(
      //sort의 파라미터로 순서를 정해주는 함수를 넣어줌
      (content1, content2) => {
        return content1.stars <= content2.stars ? 1 : -1;
      },
    );
    let slicedContents = contents.slice(0, 4);
  }

  return (
    <>
      <MainViewerBlock>
        {!loading && contents && (
          <FeaturedWorks featuredWorks={slicedContents} />
        )}
      </MainViewerBlock>
      <Spacer />
    </>
  );
};

export default MainViewer;
