import React from 'react';
import styled from 'styled-components';

const SliderHolder = styled.div`
  width: 100%;
  height: 25rem;
  border: 2px solid red;
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;
`;

const SlideItem = styled.div`
  height: 80%;
  boder: 2px solid blue;
  min-width: 100%;
  position: relative;
`;

const SlideNavButton = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 10%;
  height: 80%;
  .leftButton {
    left: 0;
  }
  .rightButton {
    right: 0;
  }
`;

const MainSlideShow = () => {
  let sliderArray = [1, 2, 3, 4, 5];

  return (
    <SliderHolder>
      {sliderArray.map((item, index) => {
        return <SlideItem key={index}>{item}</SlideItem>;
      })}
      <SlideNavButton className="leftButton">left</SlideNavButton>
      <SlideNavButton className="rightButton">right</SlideNavButton>
    </SliderHolder>
  );
};

export default MainSlideShow;
