import React from 'react';
import styled from 'styled-components';
import Responsive from '../common/Responsive';
import Button from '../common/Button';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';

const BackgroundHolder = styled.div`
  width: 100%;
  background: ${palette.gray[0]};
  padding-top: 3rem;
`;

const PostListBlock = styled(Responsive)``;

const WritePostButtonWrapper = styled.div`
  display: flex;
  justify-content: flex-end;
  margin-bottom: 3rem;
`;

const PostItemLink = styled(Link)`
  display: flex;
`;

const PostItemBlock = styled.div`
  color: ${palette.gray[7]};
  border-bottom: 2px solid ${palette.gray[2]};
  width: 100%;
  background: white;
  margin-bottom: 1rem;
  word-break: break-all;
  padding: 1rem;

  h2 {
    font-weight: 500;
  }
`;

const TitleInfoHolder = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  h2 {
    margin-top: 2px;
    font-size: 2rem;
    &:hover {
      color: ${palette.gray[7]};
    }
  }
`;

const StatusBox = styled.div`
  height: 1.6rem;
  color: #ff4e50;
  margin-bottom: 2rem;
  border-radius: 20px;
  border: 1px solid #ff4e50;
  text-align: center;
  width: 5rem;
  font-size: 0.8rem;
  padding-top: 2px;
`;

const PostItem = ({ post }) => {
  const { user, title, category, status, description, _id } = post;
  return (
    <PostItemLink to={`/@${user.username}/${_id}`}>
      <PostItemBlock>
        <div style={{ color: palette.gray[7], marginBottom: '0px' }}>
          #{category}
        </div>
        <TitleInfoHolder>
          <h2>{title}</h2>
          <StatusBox>{status}</StatusBox>
        </TitleInfoHolder>

        <p>{description}</p>
      </PostItemBlock>
    </PostItemLink>
  );
};

const PostList = ({ posts, loading, error, showWriteButton }) => {
  if (error) {
    return <PostListBlock>ERROR!</PostListBlock>;
  }
  return (
    <BackgroundHolder>
      <PostListBlock>
        <WritePostButtonWrapper>
          {showWriteButton && (
            <Button toDefaultColor to="/write">
              대회 개최
            </Button>
          )}
        </WritePostButtonWrapper>
        {!loading && posts && (
          <div>
            {posts.map((post) => {
              return <PostItem post={post} key={post._id} />;
            })}
          </div>
        )}
      </PostListBlock>
    </BackgroundHolder>
  );
};

export default PostList;
