import React, { useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import Admin from '../../components/administer/Admin';
import { listUsers } from '../../modules/users';
import userRoleUpdateButtons from '../../components/administer/UserRoleUpdateButtons';
import UserRoleUpdateButtons from '../../components/administer/UserRoleUpdateButtons';

const AdminContainer = ({ location }) => {
  const dispatch = useDispatch();
  //users: 유저 정보 리스트, user: 현재 로그인 한 유저 정보
  const { users, error, loading, user } = useSelector(
    ({ users, loading, user }) => ({
      users: users.users,
      error: users.error,
      loading: loading['users/LIST_USERS'],
      user: user.user,
    }),
  );

  useEffect(() => {
    dispatch(listUsers());
  }, [dispatch, location.search]);

  return (
    <Admin
      loading={loading}
      error={error}
      users={users}
      user={user}
      userRoleUpdateButtons={<UserRoleUpdateButtons />}
    />
  );
};

export default withRouter(AdminContainer);
