import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';
import { Link } from 'react-router-dom';
import subMenuImg from '../../images/subMenu.png';

//배경화면을 위해 responsive div를 감싸는 wrapper
const MainSubMenuBackground = styled.div`
  width: 100%;
  @media (max-width: 768px) {
    margin-top: 2rem;
  }
  border: 1px solid black;
`;

const BackgroundHolder = styled.img`
  position: absolute;
  bottom: 0;
  width: 100%;
  height: auto;
`;

//responsive design을 위한 wrapper
const MainSubMenuHolder = styled(Responsive)`
  display: flex;
  justify-content: space-between;
  @media (max-width: 1152px) {
    height: 15rem;
  }
  @media (max-width: 768px) {
    height: 10rem;
  }
`;

const SubMenuItemHolder = styled.div`
  width: 17%;
  margin: 2rem 0;
  border: 1px solid green;
`;

//원의 가로 세로 비율을 맞추기 위해 각각의 SubMenuCircle을 감싸는 div를 따로 설계
const SubMenuCircleHolder = styled.div`
  position: relative;
  width: 100%;
  padding-top: 100%;
  border: 1px solid red;
`;

//클릭시 해당 메뉴로 이동하는 원모양 SubMenu 아이템들.
const SubMenuCircle = styled(Link)`
  display: flex;
  position: absoute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  border: 1px solid blue;
  border-radius: 50%;
`;

//각각의 SubMenuCircle에 대한 설명을 감싸는 div
const SubMenuItemTitleHolder = styled.div`
  width: 100%;
  text-align: center;
  margin-top: 1rem;
  h1 {
    font-size: 1.125rem;
    margin: 0;
    font-weight: 1000;
    color: ${palette.gray[9]};
    @media (max-width: 1152px) {
      font-size: 1rem;
    }
  }
  p {
    font-size: 0.9rem;
    margin: 0;
    color: ${palette.gray[7]};
    @media (max-width: 1152px) {
      display: none;
    }
  }
`;

const MainSubMenuItem = ({ title, body, url }) => {
  return (
    <SubMenuItemHolder>
      <SubMenuCircleHolder>
        <SubMenuCircle to={url} />
      </SubMenuCircleHolder>
      <SubMenuItemTitleHolder>
        <h1>{title}</h1>
        <p>{body}</p>
      </SubMenuItemTitleHolder>
    </SubMenuItemHolder>
  );
};

const MainSubMenu = () => {
  const subMenuArray = [
    {
      title: '대회 목록',
      body: '지금까지 개최된 대회 목록',
      url: '/postlist',
    },
  ];
  return (
    <MainSubMenuBackground>
      <BackgroundHolder src={subMenuImg} alter="subMenuBackgroundImage" />
      <MainSubMenuHolder>
        {subMenuArray.map((item) => (
          <MainSubMenuItem title={item.title} body={item.body} url={item.url} />
        ))}
      </MainSubMenuHolder>
    </MainSubMenuBackground>
  );
};

export default MainSubMenu;
