import React, { useState } from 'react';
import styled from 'styled-components';

const SliderHolder = styled.div`
  width: 100%;
  height: 25rem;
  border: 2px solid red;
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;
`;

const SlideItem = styled.div`
  height: 80%;
  boder: 2px solid blue;
  min-width: 100%;
  position: relative;
  transition: 0.5s;
`;

const SlideNavButtonLeft = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 10%;
  height: 80%;
  background: none;
  left: 0;
`;

const SlideNavButtonRight = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 10%;
  height: 80%;
  background: none;
  right: 0;
`;

const MainSlideShow = () => {
  let sliderArray = [1, 2, 3, 4, 5];
  const [x, setX] = useState(0);
  const goLeft = () => {
    setX(x + 100);
  };
  const goRight = () => {
    setX(x - 100);
  };

  return (
    <SliderHolder>
      {sliderArray.map((item, index) => {
        return (
          <SlideItem key={index} style={{ transform: `translateX(${x}%)` }}>
            {item}
          </SlideItem>
        );
      })}
      <SlideNavButtonLeft className="leftButton" onClick={goLeft}>
        left
      </SlideNavButtonLeft>
      <SlideNavButtonRight className="rightButton" onClick={goRight}>
        right
      </SlideNavButtonRight>
    </SliderHolder>
  );
};

export default MainSlideShow;
