import React from 'react';
import styled from 'styled-components';
import Responsive from '../common/Responsive';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';

const ContentListBlock = styled(Responsive)`
  margin-top: 3rem;
  display: flex;
  justyify-content: space-between;
  flex-wrap: wrap;
  .contentItemBlock {
    background-color: black;
    width: 25%;
  }
`;

const ContentItemBlock = styled.div`
  border: 1px solid ${palette.gray[4]};
  border-radius: 5px;
  margin-bottom: 2rem;
  word-break: break-all;
  padding: 1rem;
  box-shadow: -1px 1px 3px 0px rgba(0, 0, 0, 0.5);
  p {
    margin-top: 2rem;
  }
`;

const TitleInfoHolder = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  h2 {
    margin-top: 2px;
    font-size: 2rem;
    &:hover {
      color: ${palette.gray[7]};
    }
  }
`;

const StatusBox = styled.div`
  height: 1.6rem;
  color: #ff4e50;
  margin-bottom: 2rem;
  border-radius: 20px;
  border: 1px solid #ff4e50;
  text-align: center;
  width: 5rem;
  font-size: 0.8rem;
  padding-top: 2px;
`;

const ContentItem = ({ content }) => {
  const { title, taggedContest, status, body, _id } = content;
  return (
    <ContentItemBlock className="contentItemBlock">
      <div>#{taggedContest}</div>
      <TitleInfoHolder>
        <h2>
          <Link to={`/content/${_id}`}>{title}</Link>
        </h2>
        <StatusBox>{status}</StatusBox>
      </TitleInfoHolder>

      <p>{body}</p>
    </ContentItemBlock>
  );
};

const ContentList = ({ contents, loading, error, showWriteButton }) => {
  if (error) {
    return <ContentListBlock>ERROR!</ContentListBlock>;
  }
  return (
    <ContentListBlock>
      {!loading && contents && (
        <div>
          {contents.map(content => {
            return (
              <ContentItem
                className="ContentItem"
                content={content}
                key={content._id}
              />
            );
          })}
        </div>
      )}
    </ContentListBlock>
  );
};

export default ContentList;
