import React, { Component } from 'react';
import Disqus from 'disqus-react';
import './Disqus.css';

export default class extends Component {
  render() {
    const contentId = this.props._id;
    const contentTitle = this.props.title;

    const disqusShortname = 'ideahub-test'; //found in your Disqus.com dashboard
    const disqusConfig = {
      url: 'http://localhost:3000', //this.props.pageUrl
      identifier: contentId, //this.props.uniqueId
      title: contentTitle, //this.props.title
    };

    return (
      <div className="article-container">
        <Disqus.DiscussionEmbed
          shortname={disqusShortname}
          config={disqusConfig}
        />
      </div>
    );
  }
}
