//수상 작품 등록을 수행하는 컴포넌트

import React from 'react';
import styled from 'styled-components';
import Button from '../common/Button';
import palette from '../../lib/styles/palette';

//등록 버튼 클릭 시 나타나는 반투명한 background
const TranslucentBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  //r, g, b, 투명도
  background: rgba(0, 0, 0, 0.25);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 30;
`;

//PrizeModal을 담을 div블록
const PrizeModalHolder = styled.div`
  width: 1000px;
  background: white;
  padding: 3rem;
  box-shadow: 0px 0px 8px rgba(0, 0, 0, 0.125);
  h1 {
    margin: 0;
    font-weight: 100;
  }
`;

const ButtonHolder = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`;

const ModalButton = styled(Button)`
  height: 2.5rem;
  width: 7rem;
  font-size: 1rem;
  margin-left: 5px;
  border-radius: 5px;
  font-weight: 100;
`;

//아이템들 전체를 감쌈
const WholeItemHoler = styled.div`
  width: 100%;
  min-height: 400px;
  max-height: 1000px;
  background: ${palette.gray[1]};
  margin: 2rem 0;
  padding: 1rem;
  overflow-y: scroll;
`;

//아이템 하나 하나를 감쌈
const ItemHolder = styled.div`
  width: 100%;
  height: 5rem;
  background: white;
  border: 1px solid ${palette.mainColor};
  border-radius: 5px;
  margin-bottom: 0.5rem;
`;

const PrizedContentItem = () => {};

//수상 작품 등록을 수행하는 컴포넌트
//부모!!!!! NoPrizedAlerter 컴포넌트 (./NoPrizedAlerter.js)
const PrizeModal = ({ visible, onConfirm, onCancel }) => {
  if (!visible) return null;
  return (
    <TranslucentBackground>
      <PrizeModalHolder>
        <h1>수상 작품 등록</h1>
        <WholeItemHoler>
          <ItemHolder />
          <ItemHolder />
          <ItemHolder />
        </WholeItemHoler>
        <ButtonHolder>
          <ModalButton onClick={onConfirm} toDefaultColor>
            등록
          </ModalButton>
          <ModalButton onClick={onCancel}>취소</ModalButton>
        </ButtonHolder>
      </PrizeModalHolder>
    </TranslucentBackground>
  );
};

export default PrizeModal;
