import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';
//import PostInfoSide from './PostInfoSide';

const ContentViewerBlock = styled(Responsive)`
  width: 852px;

  @media (max-width: 852px) {
    width: 468px;
  }
  @media (max-width: 468px) {
    width: 100%;
  }
`;

const ContentHead = styled.div`
  border-bottom: 1px solid ${palette.gray[2]};
  padding-bottom: 2rem;
  margin-bottom: 2rem;
  h1 {
    font-size: 2rem;
    margin: 0;
  }
`;

const SubContents = styled.div`
  color: ${palette.gray[7]};
  margin-bottom: 0.3rem;
`;

const ContentContent = styled.div`
  font-size: 1.3125rem;
  color: ${palette.gray[8]};

  img {
    resizemode: contain;
    width: 820px;

    @media (max-width: 852px) {
      width: 468px;
    }
    @media (max-width: 468px) {
      width: 100%;
    }
  }
`;

const ContentsHolder = styled(Responsive)`
  display: flex;
  margin-top: 4rem;
  margin-bottom: 10rem;
`;

const ContentViewer = ({ content, error, loading }) => {
  if (error) {
    if (error.response && error.response.status === 404) {
      return <ContentViewerBlock>404! Content not found</ContentViewerBlock>;
    }
    return <ContentViewerBlock>error!</ContentViewerBlock>;
  }

  if (loading || !content) {
    return null;
  }

  const { title, category, status, date, place, description } = content;

  return (
    <ContentsHolder>
      <ContentViewerBlock>
        <ContentHead>
          <SubContents>카테고리 #{category}</SubContents>
          <h1>{title}</h1>
        </ContentHead>
        <ContentContent
          dangerouslySetInnerHTML={{
            __html: description,
          }}
        />
      </ContentViewerBlock>
      <ContentInfoSide
        title={title}
        category={category}
        status={status}
        date={date}
        place={place}
      />
    </ContentsHolder>
  );
};

export default ContentViewer;
