import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import mainImg1 from '../../images/main.png';
import mainImg2 from '../../images/main3.png';

const SliderHolder = styled.div`
  width: 100%;
  height: 25rem;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;
  overflow: hidden;
`;

const SlideItem = styled.div`
  height: 100%;
  min-width: 100%;
  position: relative;
  transition: 0.5s;
  overflow: hidden;
`;

const ImgHolder = styled.img`
  width: 100+'%';
  height: auto;
`;

const SlideNavButtonLeft = styled.button`
  border: none;
  outline: none;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 10%;
  height: 80%;
  background: none;
  left: 0;
  transition: 0.5s;
  &:hover {
    background: rgba(0, 0, 0, 0.356);
    cursor: pointer;
  }
`;

const SlideNavButtonRight = styled.button`
  border: none;
  outline: none;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 10%;
  height: 80%;
  background: none;
  right: 0;
  transition: 0.5s;
  &:hover {
    background: rgba(0, 0, 0, 0.356);
    cursor: pointer;
  }
`;

const MainSlideShow = () => {
  let sliderArray = [
    <ImgHolder src={mainImg1} />,
    <ImgHolder src={mainImg2} />,
  ];
  const [x, setX] = useState(0);
  const goLeft = () => {
    x === 0 ? setX(-100 * (sliderArray.length - 1)) : setX(x + 100);
  };
  const goRight = () => {
    x === -100 * (sliderArray.length - 1) ? setX(0) : setX(x - 100);
  };

  const timeoutFunc = () => {
    goRight();
  };

  useEffect(() => {
    setInterval(goRight, 1000);
  }, [goRight]);

  return (
    <SliderHolder>
      {sliderArray.map((item, index) => {
        return (
          <SlideItem key={index} style={{ transform: `translateX(${x}%)` }}>
            {item}
          </SlideItem>
        );
      })}
      <SlideNavButtonLeft className="leftButton" onClick={goLeft}>
        left
      </SlideNavButtonLeft>
      <SlideNavButtonRight className="rightButton" onClick={goRight}>
        right
      </SlideNavButtonRight>
    </SliderHolder>
  );
};

export default MainSlideShow;
