import React, { useRef, useEffect, useState } from 'react';
import Quill from 'quill';
import 'quill/dist/quill.snow.css';
import styled from 'styled-components';
//import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';

const EditorBlock = styled(Responsive)`
  padding-top: 5rem;
`;

const StyledInput = styled.input`
  padding: 0.5rem;
  width: 100%;
`;
const QuillWrapper = styled.div`
  boder-width: 1px;
  margin-top: 2rem;
  margin-bottom: 1rem;

  .ql-editor {
    padding: 0;
    min-height: 320px;
    line-height: 1.5;
  }
  .ql-editor-ql-blank::before {
    left: 0px;
  }
`;

const OptionTitleHolder = styled.div`
  width: 12rem;
  @media (max-width: 768px) {
    width: 50%;
  }
`;

const OptionBodyHolder = styled.div`
  width: 50%;
`;

const Holder = styled.div`
  margin-top: 2rem;
  margin-bottom: 2rem;
  display: flex;
  align-items: center;
  //justify-content: space-between;
  width: 100%;
`;

const ContentEditor = ({
  title,
  body,
  videoURL,
  team,
  onChangeField,
  taggedContest,
}) => {
  //videoURL 옵션을 보여줄지 결정.
  const [showOption, setShowOption] = useState(false);
  const onChangeShowOption = () => {
    setShowOption(!showOption);
  };

  const quillElement = useRef(null);
  const quillInstance = useRef(null);

  useEffect(() => {
    quillInstance.current = new Quill(quillElement.current, {
      theme: 'snow',
      placeholder: '작품 소개 입력.',
      modules: {
        toolbar: [
          [{ header: '1' }, { header: '2' }],
          ['bold', 'italic', 'underline', 'strike'],
          [{ list: 'ordered' }, { list: 'bullet' }],
          ['blockquote', 'code-block', 'link', 'image'],
        ],
      },
    });

    const quill = quillInstance.current;
    quill.on('text-change', (delta, oldDelta, source) => {
      if (source === 'user') {
        onChangeField({ key: 'body', value: quill.root.innerHTML });
      }
    });
  }, [onChangeField]);

  const mounted = useRef(false);
  useEffect(() => {
    if (mounted.current) return;
    mounted.current = true;
    quillInstance.current.root.innerHTML = body;
  }, [body]);

  const onChangeTitle = (e) => {
    onChangeField({ key: 'title', value: e.target.value });
  };

  // const onChangetaggedContest = (e) => {
  //   onChangeField({ key: 'taggedContest', value: e.target.value });
  // };

  const onChangevideoURL = (e) => {
    onChangeField({ key: 'videoURL', value: e.target.value });
  };

  const onChangeTeam = (e) => {
    onChangeField({ key: 'team', value: e.target.value });
  };

  const onChangeStatus = (e) => {
    onChangeField({ key: 'status', value: e.target.value });
  };

  return (
    <EditorBlock>
      <Holder>
        <OptionTitleHolder>참가 대회</OptionTitleHolder>
        <p style={{ width: '30rem', color: 'gray' }}>{taggedContest}</p>
      </Holder>

      <Holder>
        <OptionTitleHolder>작품 이름</OptionTitleHolder>
        <OptionBodyHolder>
          <StyledInput
            placeholder="이름"
            onChange={onChangeTitle}
            value={title}
          />
        </OptionBodyHolder>
      </Holder>

      <Holder>
        <OptionTitleHolder>상태</OptionTitleHolder>
        <OptionBodyHolder>
          <select onChange={onChangeStatus}>
            <option value="garbageOption" disabled selected>
              ==선택==
            </option>
            <option value="준비중">준비중</option>
            <option value="개발중">개발중</option>
            <option value="개발 종료">개발 종료</option>
          </select>
        </OptionBodyHolder>
      </Holder>

      <Holder>
        <OptionTitleHolder>팀원</OptionTitleHolder>
        <OptionBodyHolder>
          <StyledInput
            placeholder="홍길동, 철수, 영희..."
            onChange={onChangeTeam}
            value={team}
          />
        </OptionBodyHolder>
      </Holder>

      <Holder>
        <OptionTitleHolder>동영상 등록 여부</OptionTitleHolder>
        <OptionBodyHolder>
          <form>
            <label>
              <input
                type="radio"
                name="video"
                checked="checked"
                onChange={onChangeShowOption}
              />
              미등록
            </label>
            <label>
              <input type="radio" name="video" onChange={onChangeShowOption} />
              등록
            </label>
          </form>
        </OptionBodyHolder>
      </Holder>
      {showOption ? (
        <StyledInput
          placeholder="유튜브 동영상 ID"
          onChange={onChangevideoURL}
          value={videoURL}
        />
      ) : null}
      <hr style={{ width: '100%', marginBottom: '2rem' }} />
      <div>세부 사항</div>
      <QuillWrapper>
        <div ref={quillElement} />
      </QuillWrapper>
    </EditorBlock>
  );
};

export default ContentEditor;
