import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import Responsive from '../common/Responsive';
import ContentInfoSide from './ContentInfoSide';
import Button from '../common/Button';
import CommentsViewer from './CommentsViewer';
import CommentsWriter from './CommentsWriter';
import CommentsWriterContainer from '../../containers/content/CommentsWriterContainer';
import Disqus from '../disqus/Disqus';

const CommentWrapper = styled.div`
  width: 100%;
  border-top: 1px solid ${palette.gray[5]};
  margin-top: 3rem;
  padding-top: 1rem;
  align-items: center;
  justify-content: space-between;

  h2 {
    font-size: 20px;
  }
`;

const ContentViewerBlock = styled(Responsive)`
  width: 852px;

  @media (max-width: 1152px) {
    width: 100%;
  }
  @media (max-width: 468px) {
    width: 100%;
  }
`;

const ContentHead = styled.div`
  border-bottom: 1px solid ${palette.gray[2]};
  padding-bottom: 2rem;
  margin-bottom: 2rem;
  h1 {
    font-size: 2rem;
    margin: 0;
  }
`;

const SubContents = styled.div`
  color: ${palette.gray[7]};
  margin-bottom: 0.3rem;
`;

const ContentContent = styled.div`
  font-size: 1.3125rem;
  color: ${palette.gray[8]};

  img {
    resizemode: contain;
    width: 820px;

    @media (max-width: 852px) {
      width: 468px;
    }
    @media (max-width: 468px) {
      width: 100%;
    }
  }
`;

const ContentsHolder = styled(Responsive)`
  display: flex;
  margin-top: 4rem;
  margin-bottom: 10rem;
  @media (max-width: 1152px) {
    flex-direction: column;
  }
`;

const TitleArea = styled.div`
  display: flex;
  justify-content: space-between;
  h1 {
    font-size: 2rem;
    margin: 0;
  }
`;

const StarBox = styled.div`
  display: flex;
  border: solid 1px;
  border-radius: 5px;
  height: 2rem;
  border-color: ${palette.gray[5]};
  button {
    background: ${palette.gray[2]};
    fontsize: 16px;
    color: black;
    &:hover {
      background: ${palette.gray[3]};
    }
  }
  div {
    font-size: 16px;
    padding: 0.2rem 0.5rem;
  }
`;

const ContentViewer = ({ content, error, loading, onChangeComment }) => {
  if (error) {
    if (error.response && error.response.status === 404) {
      return <ContentViewerBlock>404! Content not found</ContentViewerBlock>;
    }
    return <ContentViewerBlock>error!</ContentViewerBlock>;
  }

  if (loading || !content) {
    return null;
  }

  const {
    id,
    title,
    body,
    taggedContest,
    videoURL,
    team,
    status,
    stars,
    comments,
  } = content;

  const onChangeCommentBody = event => {
    onChangeComment({ key: 'commentBody', value: event.target.value });
  };

  return (
    <ContentsHolder>
      <ContentViewerBlock>
        <ContentHead>
          <SubContents>#{taggedContest}</SubContents>
          <TitleArea>
            <h1>{title}</h1>
            <StarBox>
              <Button>star</Button>
              <div>{stars}</div>
            </StarBox>
          </TitleArea>
        </ContentHead>

        <iframe
          width="100%"
          height="480"
          src={videoURL}
          frameborder="0"
          allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
          allowfullscreen
        ></iframe>

        <ContentContent
          dangerouslySetInnerHTML={{
            __html: body,
          }}
        />

        <CommentWrapper>
          <h2 style={{ fontWeight: '500' }}>Comments</h2>
          <CommentsViewer comments={comments} />
          <CommentsWriterContainer onChangeCommentBody={onChangeCommentBody} />
        </CommentWrapper>
        <Disqus id={id} title={title} />
      </ContentViewerBlock>
      <ContentInfoSide
        className="sideInfo"
        title={title}
        taggedContest={taggedContest}
        status={status}
        team={team}
        stars={stars}
      />
    </ContentsHolder>
  );
};

export default ContentViewer;
