//메인화면의 슬라이드에 표시될 타이틀 화면

import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';

const TitleSlideHolder = styled(Link)`
  width: 100%;
  text-align: center;
  background: ${palette.gray[5]};
  color: ${palette.gray[8]};
  h1 {
    margin: 0;
    font-size: 2.75rem;
  }
  p {
    font-size: 1.3rem;
    font-weight: 100;
    margin: 0;
  }
  .coloredH1 {
    color: ${palette.mainColor};
  }
`;

const TitleSlide = () => {
  return (
    <TitleSlideHolder to="/about">
      <h1>전북대학교 작품 전시 및 공유 서비스</h1>
      <h1 className="coloredH1">IDEA HUB</h1>
      <p>
        IDEA HUB는 여러분의 작품을 전시하고, 참여를 유도하기 위해
        만들어졌습니다.
      </p>
      <p>개최중인 대회에 직접 작품을 등록해보고, 다른 사람과 공유해보세요.</p>
    </TitleSlideHolder>
  );
};

export default TitleSlide;
