import client from './client';

//user 목록을 불러옴
export const listUsers = () => client.get('/api/users');
//유저 정보 업데이트. 현재는 user role만 변경할 수 있도록.
export const updateUsers = ({ _id, role }) =>
  client.patch('/api/users', { _id, role });
