//수상 작품 목록이 등록되어 있을 경우, 그 목록을 보여주는 컴포넌트.

import React from 'react';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import Responsive from '../common/Responsive';
import prizeLeft from '../../images/prizeLeft.png';
import prizeRight from '../../images/prizeRight.png';

const PrizedListHolder = styled(Responsive)`
  margin-top: 3rem;
  display: inline;
  align-content: center;
`;

const PrizedItemHolder = styled(Link)`
  width: 100%;
  float: right;
  padding: 1rem;
  border: 1px solid ${palette.mainColor};
  border-radius: 5px;
  margin-bottom: 0.5rem;
  word-break: break-all;
  background: white;
  color: black;
  p {
    margin-top: 2rem;
    margin-bottom: 2rem;
  }
`;

//대회 제목 & '수상 작품 목록' 글자 홀더
const TitleHolder = styled.div`
  @font-face {
    font-family: 'MapoGoldenPier';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2001@1.1/MapoGoldenPierA.woff')
      format('woff');
    font-weight: normal;
    font-style: normal;
  }

  text-align: center;
  color: ${palette.gray[8]};

  h1 {
    font-size: 3rem;
    margin: 0;
    font-family: 'MapoGoldenPier';
    font-weight: 500;
    margin-top: 2rem;
    @media (max-width: 1152px) {
      font-size: 2rem;
      margin-top: 0.5rem;
    }
    @media (max-width: 768px) {
      font-size: 1rem;
      margin-top: 0.8rem;
    }
  }
  h2 {
    font-size: 6rem;
    margin: 0;
    font-family: 'MapoGoldenPier';
    font-weight: 500;
    @media (max-width: 1152px) {
      font-size: 4rem;
    }
    @media (max-width: 768px) {
      font-size: 2rem;
    }
  }
`;

const TitleImgHolder = styled.div`
  width: 100%;
  display: flex;
  margin: 5rem 0;
  justify-content: center;
  img {
    height: 15rem;
    @media (max-width: 1152px) {
      height: 8rem;
    }
    @media (max-width: 768px) {
      height: 5rem;
    }
  }
`;

//수상 순위와 그 순위에 해당하는 작품들을 감싸는 div
const ListByPrizeHolder = styled.div`
  display: flex;
`;

//수상 순위를 표현
const PrizePriority = styled.div`
  height: 100%;
  border-right: 3px solid ${palette.mainColor};
  margin-right: 2rem;
  h1 {
    font-weight: 500;
    height: 100%;
    color: ${palette.mainColor};
    margin: 0;
    margin-right: 2rem;
  }
`;

//prizedItem는 post 객체 내의 배열 prized의 각각의 element를 나타냄.
const PrizedItem = ({ prizedItem }) => {
  const { _id, title, team } = prizedItem;
  return (
    <PrizedItemHolder to={`/content/${_id}`}>
      <h1>{title}</h1>
      <h2>{team}</h2>
    </PrizedItemHolder>
  );
};

//prized는 post 객체 내의 배열 prized. prizedItem 객체의 배열임.
//수정: 해당 대회에 속한 작품들의 리스트인 'contentList' 에서, 우선순위가 정해진 작품들만 로드

const PrizedList = ({ prized, post }) => {
  const { title } = post;
  return (
    <div>
      <TitleImgHolder>
        <img src={prizeLeft} alter="prizeLeft" />
        <TitleHolder>
          <h1>{title}</h1>
          <h2>수상 작품 목록</h2>
        </TitleHolder>
        <img src={prizeRight} alter="prizeRight" />
      </TitleImgHolder>
      <PrizedListHolder>
        <ListByPrizeHolder>
          <PrizePriority>
            <h1>1 위</h1>
          </PrizePriority>
          <div>
            {prized.map(prizedItem => {
              return (
                <PrizedItem prizedItem={prizedItem} key={prizedItem._id} />
              );
            })}
          </div>
        </ListByPrizeHolder>
      </PrizedListHolder>
    </div>
  );
};

export default PrizedList;
