import { createAction, handleActions } from 'redux-actions';
import createRequestSaga, {
  createRequestActionTypes,
} from '../lib/createRequestSaga';
import * as postsAPI from '../lib/api/posts';
import { takeLatest } from 'redux-saga/effects';

//===========Action Types==============

/*
  READ_POST = 'post/READ_POST';
  READ_POST_SUCCESS = 'post/READ_POST_SUCCESS';
  READ_POST_FAILURE = 'post/READ_POST_FAILURE';
*/
const [
  READ_POST,
  READ_POST_SUCCESS,
  READ_POST_FAILURE,
] = createRequestActionTypes('post/READ_POST');
const UNLOAD_POST = 'post/UNLOAD_POST'; //flushes data, leaving post page.
const GET_CONTETNS_LIST = 'post/GET_CONTETNS_LIST'; //해당 대회에 등록되어있는 작품 목록을 가져옴.

//===========Action 생성 함수==============

export const readPost = createAction(READ_POST, id => id);
export const unloadPost = createAction(UNLOAD_POST);
export const getContentsList = createAction(GET_CONTETNS_LIST);

//===========사가 생성==============

const readPostSaga = createRequestSaga(READ_POST, postsAPI.readPost);
const getContentsListSaga = createRequestSaga(
  GET_CONTETNS_LIST,
  postsAPI.getContentsList,
);
export function* postSaga() {
  yield takeLatest(READ_POST, readPostSaga);
  yield takeLatest(GET_CONTETNS_LIST, getContentsListSaga);
}

//===========initial state============
const initialState = {
  post: null,
  error: null,
  contentsList: null,
};

//==============reducer================
const post = handleActions(
  {
    //actionType: (state, action) => newState
    [READ_POST_SUCCESS]: (state, { payload: post }) => ({
      ...state,
      post,
    }),
    [READ_POST_FAILURE]: (state, { payload: error }) => ({
      ...state,
      error,
    }),
    //flushes data, leaving post page.
    [UNLOAD_POST]: () => initialState,
    [GET_CONTETNS_LIST]: (state, { payload: contentsList }) => ({
      ...state,
      contentsList,
    }),
  },
  initialState,
);

export default post;
