import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';

const UserRoleUpdateButtonsHolder = styled.div`
  margin: 0.5rem 0;
  display: flex;
`;

const ChangeButton = styled.button`
  padding: 0.5rem;
  background: ${palette.mainColor};
  color: white;
  border: none;
  border-radius: 5px;
  margin-right: 2px;

  &:hover {
    background: ${palette.mainColorHovered};
  }
`;

const UserRoleUpdateButtons = () => {
  return (
    <UserRoleUpdateButtonsHolder>
      <ChangeButton>ADMIN</ChangeButton>
      <ChangeButton>WRITER</ChangeButton>
      <ChangeButton>VISITER</ChangeButton>
    </UserRoleUpdateButtonsHolder>
  );
};

export default UserRoleUpdateButtons;
