import React, { useEffect } from 'react';
import CommentsWriter from '../../components/content/CommentsWriter';
import { useSelector, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import submitComment from '../../modules/content';

const CommentsWriterContainer = ({ history, onChangeCommentBody }) => {
  const dispatch = useDispatch();
  const {
    title,
    body,
    taggedContest,
    videoURL,
    team,
    status,
    stars,
    comments,
    user,
  } = useSelector(({ content, user }) => ({
    title: content.content.title,
    body: content.content.body,
    taggedContest: content.content.taggedContest,
    videoURL: content.content.videoURL,
    team: content.content.team,
    status: content.content.status,
    stars: content.content.stars,
    comments: [...content.content.comments, content.commentBody],
    user: user.user,
  }));

  const onAddComment = () => {
    if (user) {
      alert('댓글 작성 완료');
      dispatch(
        submitComment({
          title,
          body,
          taggedContest,
          videoURL,
          team,
          status,
          stars,
          comments,
        }),
      );
    } else {
      alert('로그인이 필요한 서비스 입니다.');
      history.push('/login');
    }
  };
  return (
    <CommentsWriter
      onAddComment={onAddComment}
      onChangeCommentBody={onChangeCommentBody}
    />
  );
};

export default withRouter(CommentsWriterContainer);
