import React from 'react';
import styled from 'styled-components';

const SliderHolder = styled.div`
  width: 100%;
  height: 25rem;
  border: 2px solid red;
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;
  .slide {
    boder: 2px solid blue;
    min-width: 100%;
    height: 20rem;
    color: black;
  }
`;

const SlideNavButton = styled.button`
  .leftButton {
    position: absolute;
    top: 50%;
    left: 0;
    transform: translateY(-50%);
    width: 5%;
    height: 80%;
  }
`;

const MainSlideShow = () => {
  let sliderArray = [1, 2, 3, 4, 5];

  return (
    <SliderHolder>
      {sliderArray.map((item, index) => {
        return (
          <div key={index} className="slide">
            {item}
          </div>
        );
      })}
      <SlideNavButton className="leftButton">left</SlideNavButton>
      <SlideNavButton>right</SlideNavButton>
    </SliderHolder>
  );
};

export default MainSlideShow;
