import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import profile1 from '../../images/profile1.png';

const ItemWrapper = styled.div`
  display: flex;
  margin-top: 1rem;
  margin-bottom: 1rem;
`;

const CommentTitle = styled.div`
  display: flex;
  h2 {
    font-weight: 500;
    margin: 2px;
  }
  h3 {
    margin-left: 0.5rem;
    font-size: 0.7rem;
    font-weight: 400;
    color: ${palette.gray[6]};
  }
`;

const ProfileImage = styled.img`
  width: 3rem;
  height: 3rem;
`;

const CommentBody = styled.div``;

const profileGenerator = () => {
  const random = Math.ceil(Math.random(0, 4));
  return `../../images/profile${random}.png`;
};

const CommentItem = ({ comment }) => {
  //const { username, commentBody, commentPublishDate } = comment;
  return (
    <ItemWrapper>
      <ProfileImage src={profile1} alt="ProfileImage" />
      <div>
        <CommentTitle>
          <h2>sangseok</h2>
          <h3>2019</h3>
        </CommentTitle>
        <CommentBody>와우 정말 멋져요</CommentBody>
      </div>
    </ItemWrapper>
  );
};

const CommentsViewer = ({ comments, loading, error, showWriteButton }) => {
  return (
    <div>
      {/* {comments.map(comment => {
         return <CommentItem comment={comment} />;
       })} */}
      <CommentItem />
      <CommentItem />
      <CommentItem />
    </div>
  );
};

export default CommentsViewer;
