import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import mainImg1 from '../../images/main.png';
import mainImg2 from '../../images/main3.png';

import TitleSlide from './TitleSlide';

const SliderHolder = styled.div`
  height: 30rem;
  padding: 0;
  display: flex;
  align-items: center;
  box-sizing: border-box;
  margin-top: 2rem;
  overflow: hidden;
  @media (max-width: 768px) {
    display: none;
  }
`;

const SlideItem = styled.div`
  position: relative;
  height: 100%;
  min-width: 100%;
  transition: 0.5s;
`;

const ImgHolder = styled.img`
  width: 100%;
  height: auto;
`;

const SlideNavButtonLeft = styled.button`
  color: ${palette.gray[7]};
  border: none;
  outline: none;
  position: absolute;
  width: 10%;
  height: 30rem;
  background: none;
  left: 0;
  transition: 0.5s;
  font-size: 4rem;
  font-weight: 100;
  &:hover {
    background: rgba(0, 0, 0, 0.356);
    cursor: pointer;
    color: white;
  }
`;

const SlideNavButtonRight = styled.button`
  color: ${palette.gray[7]};
  border: none;
  outline: none;
  position: absolute;
  width: 10%;
  height: 30rem;
  background: none;
  right: 0;
  transition: 0.5s;
  font-size: 4rem;
  font-weight: 100;
  font-height: 30rem;
  &:hover {
    background: rgba(0, 0, 0, 0.356);
    cursor: pointer;
    color: white;
  }
`;

const ButtonSliderHolder = styled.div`
  positon: absolute;
  bottom: 0;
  height: 2rem;
  width: 2rem;
  border: 1px solid blue;
`;

const MainSlideShow = () => {
  let sliderArray = [
    <TitleSlide />,
    <TitleSlide />,
    <ImgHolder src={mainImg2} />,
  ];
  const [x, setX] = useState(0);
  const [isAutoSlide, setIsAutoSlide] = useState(false);
  const goLeft = () => {
    x === 0 ? setX(-100 * (sliderArray.length - 1)) : setX(x + 100);
  };
  const goRight = () => {
    x === -100 * (sliderArray.length - 1) ? setX(0) : setX(x - 100);
    console.log('x: ', x);
    console.log('go right called');
  };

  const goClicked = (clickedIndex) => {};

  console.log('처음 x: ', x);

  //슬라이드 자동 스크롤을 위한 즉시 실행 함수
  (() => {
    if (!isAutoSlide) {
      setIsAutoSlide(true);
    }
  })();

  return (
    <SliderHolder>
      {sliderArray.map((item, index) => {
        return (
          <SlideItem key={index} style={{ transform: `translateX(${x}%)` }}>
            {item}
          </SlideItem>
        );
      })}
      <SlideNavButtonLeft onClick={goLeft}>&#123;</SlideNavButtonLeft>
      <SlideNavButtonRight onClick={goRight}>}</SlideNavButtonRight>
      <ButtonSliderHolder />
    </SliderHolder>
  );
};

export default MainSlideShow;
