import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';
import { Link } from 'react-router-dom';

const MainSubMenuBarHolder = styled.div`
  display: flex;
  width: 100%;
  height: 15rem;
  border: 1px solid black;
  @media (max-width: 768px) {
    display: none;
  }
`;

const MainSubMenuBarItemHolder = styled(Link)`
  height: 100%;
  width: 50%;
  text-align: center;
  color: white;
  font-size: 1.5rem;
  background-size: 100% auto;
  background-position: center center;
  h1 {
    font-size: 3rem;
    font-weight: 100;
  }
  &:hover {
    opacity: 0.85;
  }
`;

const MainSubMenuBarItem = ({ menuItem }) => {
  const { title, url, imgUrl } = menuItem;
  return (
    <MainSubMenuBarItemHolder
      to={url}
      style={{ backgroundImage: `url('${imgUrl}')` }}
    >
      <h1>{title}</h1>
      <h2>VIEW MORE</h2>
    </MainSubMenuBarItemHolder>
  );
};

const MainSubMenuBar = () => {
  //MainSubMenuBar 내용 수정시 아래 배열을 변경하세요
  const menuArray = [
    {
      title: '대회 목록',
      url: '/postlist',
      imgUrl:
        'https://cdn.pixabay.com/photo/2015/01/08/18/11/startup-593296_960_720.jpg',
    },
    {
      title: '작품 정보',
      url: 'contentlist',
      imgUrl:
        'https://cdn.pixabay.com/photo/2016/11/17/23/29/programming-1832991_960_720.png',
    },
  ];

  return (
    <MainSubMenuBarHolder>
      {menuArray.map((item) => {
        return (
          <MainSubMenuBarItem
            menuItem={item}
            style={{ width: 100 / menuArray.length }}
          />
        );
      })}
    </MainSubMenuBarHolder>
  );
};

export default MainSubMenuBar;
