import React from 'react';
import styled from 'styled-components';
import Responsive from '../common/Responsive';
import Button from '../common/Button';
import palette from '../../lib/styles/palette';
import RecommendedPosts from './RecommendedPosts';
import { Link } from 'react-router-dom';

const BackgroundHolder = styled.div`
  width: 100%;
  background: ${palette.gray[0]};
  padding: 1rem 0;
`;

const PostListBlock = styled(Responsive)`
  padding-left: 0;
  padding-right: 0;
`;

const ListRecommendedHolder = styled(Responsive)`
  display: flex;
`;

const WritePostButtonWrapper = styled(Responsive)`
  display: flex;
  justify-content: flex-end;
  margin-bottom: 2rem;
  margin-top: 1rem;
`;

const PostItemLink = styled(Link)`
  display: flex;
`;

const PostItemBlock = styled.div`
  color: ${palette.gray[7]};
  border-bottom: 2px solid ${palette.gray[2]};
  width: 100%;
  background: white;
  margin-bottom: 1rem;
  word-break: break-all;
  padding: 2rem 1.5rem;
  p {
    margin: 0;
  }
  h2 {
    font-weight: 500;
  }
`;

const TitleInfoHolder = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  h2 {
    margin: 0;
    font-size: 1.25rem;
    font-weight: 400;
    &:hover {
      color: ${palette.gray[9]};
    }
  }
`;

const StatusBox = styled.div`
  height: 1.6rem;
  color: #ff4e50;
  margin-bottom: 2rem;
  border-radius: 20px;
  border: 1px solid #ff4e50;
  text-align: center;
  width: 5rem;
  font-size: 0.8rem;
  padding-top: 2px;
`;

const PostItem = ({ post }) => {
  const { user, title, category, status, description, _id } = post;
  return (
    <PostItemLink to={`/@${user.username}/${_id}`}>
      <PostItemBlock>
        <div style={{ color: palette.gray[7], marginBottom: '0px' }}>
          #{category}
        </div>
        <TitleInfoHolder>
          <h2>{title}</h2>
          <StatusBox>{status}</StatusBox>
        </TitleInfoHolder>

        <p>{description}</p>
      </PostItemBlock>
    </PostItemLink>
  );
};

const PostList = ({ posts, loading, error, showWriteButton }) => {
  if (error) {
    return <PostListBlock>ERROR!</PostListBlock>;
  }
  return (
    <BackgroundHolder>
      <WritePostButtonWrapper>
        {showWriteButton && (
          <Button toDefaultColor to="/write">
            대회 개최
          </Button>
        )}
      </WritePostButtonWrapper>
      <ListRecommendedHolder>
        <PostListBlock>
          {!loading && posts && (
            <div>
              {posts.map((post) => {
                return <PostItem post={post} key={post._id} />;
              })}
            </div>
          )}
        </PostListBlock>
        <RecommendedPosts posts={posts} />
      </ListRecommendedHolder>
    </BackgroundHolder>
  );
};

export default PostList;
